import{_ as e,c as a,o as r,ah as i}from"./chunks/framework.BDBjiFeZ.js";const h=JSON.parse('{"title":"System Requirements","description":"","frontmatter":{"sidebar_position":1},"headers":[],"relativePath":"en_US/installation/environmental_requirement.md","filePath":"en_US/installation/environmental_requirement.md"}'),n={name:"en_US/installation/environmental_requirement.md"};function l(o,t,s,d,m,u){return r(),a("div",null,[...t[0]||(t[0]=[i('<h1 id="system-requirements" tabindex="-1">System Requirements <a class="header-anchor" href="#system-requirements" aria-label="Permalink to “System Requirements”">​</a></h1><h2 id="hardware-requirements" tabindex="-1">Hardware Requirements <a class="header-anchor" href="#hardware-requirements" aria-label="Permalink to “Hardware Requirements”">​</a></h2><h3 id="minimum-test-configuration" tabindex="-1">Minimum Test Configuration <a class="header-anchor" href="#minimum-test-configuration" aria-label="Permalink to “Minimum Test Configuration”">​</a></h3><p>ThingsPanel typically consumes less than 100MB of memory overall, but may reach several hundred MB after installing essential services. A device with 1GB memory is sufficient for testing in environments with a small number of devices.</p><table tabindex="0"><thead><tr><th style="text-align:left;">Item</th><th style="text-align:left;">Requirement</th></tr></thead><tbody><tr><td style="text-align:left;">CPU</td><td style="text-align:left;">1 Core 2.0GHz+</td></tr><tr><td style="text-align:left;">Memory</td><td style="text-align:left;">1GB</td></tr><tr><td style="text-align:left;">Disk</td><td style="text-align:left;">20GB</td></tr></tbody></table><h3 id="r-d-test-configuration" tabindex="-1">R&amp;D Test Configuration <a class="header-anchor" href="#r-d-test-configuration" aria-label="Permalink to “R&amp;D Test Configuration”">​</a></h3><table tabindex="0"><thead><tr><th style="text-align:left;">Item</th><th style="text-align:left;">Requirement</th></tr></thead><tbody><tr><td style="text-align:left;">CPU</td><td style="text-align:left;">2 Core+ 2.5GHz+</td></tr><tr><td style="text-align:left;">Memory</td><td style="text-align:left;">4GB+</td></tr><tr><td style="text-align:left;">Disk</td><td style="text-align:left;">40GB+</td></tr></tbody></table><h3 id="production-environment-configuration" tabindex="-1">Production Environment Configuration <a class="header-anchor" href="#production-environment-configuration" aria-label="Permalink to “Production Environment Configuration”">​</a></h3><p>We recommend <strong>Cluster Deployment</strong> to ensure high system availability:</p><table tabindex="0"><thead><tr><th style="text-align:left;">Item</th><th style="text-align:left;">Minimum Requirement</th><th style="text-align:left;">Recommended Configuration</th></tr></thead><tbody><tr><td style="text-align:left;">CPU</td><td style="text-align:left;">4 Core+ 2.5GHz+</td><td style="text-align:left;">8 Core+ 3.0GHz+</td></tr><tr><td style="text-align:left;">Memory</td><td style="text-align:left;">8GB+</td><td style="text-align:left;">16GB+</td></tr><tr><td style="text-align:left;">Disk</td><td style="text-align:left;">40GB+ SSD</td><td style="text-align:left;">100GB+ SSD</td></tr><tr><td style="text-align:left;">Server Count</td><td style="text-align:left;">2-3 Nodes</td><td style="text-align:left;">3-5 Nodes</td></tr></tbody></table><h4 id="cpu-architecture-support" tabindex="-1">CPU Architecture Support <a class="header-anchor" href="#cpu-architecture-support" aria-label="Permalink to “CPU Architecture Support”">​</a></h4><ul><li><strong>x86</strong>: Supports Intel, AMD, and other mainstream server CPUs.</li><li><strong>ARM</strong>: Supports Phytium, Kunpeng, and other domestic CPUs.</li><li><strong>RISC-V</strong>: Supports Zhaoxin, Hygon, and other domestic CPUs.</li></ul><h4 id="server-architecture-requirements" tabindex="-1">Server Architecture Requirements <a class="header-anchor" href="#server-architecture-requirements" aria-label="Permalink to “Server Architecture Requirements”">​</a></h4><ul><li>Supports mainstream server form factors: Tower, Rack, Blade.</li><li><strong>Rack-mounted servers</strong> are recommended for easier management and expansion.</li><li>Redundant power supplies and RAID configurations are recommended.</li></ul><h4 id="localization-domestic-adaptation-support" tabindex="-1">Localization (Domestic Adaptation) Support <a class="header-anchor" href="#localization-domestic-adaptation-support" aria-label="Permalink to “Localization (Domestic Adaptation) Support”">​</a></h4><ul><li><strong>Operating Systems</strong>: <ul><li>Supports UnionTech UOS, Kylin, and other domestic operating systems.</li><li>Supports openEuler, EulerOS, and other domestic server operating systems.</li></ul></li><li><strong>Databases</strong>: <ul><li>Supports Alibaba Cloud PolarDB, KingBase, and other domestic databases.</li></ul></li></ul><h3 id="recommendations" tabindex="-1">Recommendations <a class="header-anchor" href="#recommendations" aria-label="Permalink to “Recommendations”">​</a></h3><ul><li><strong>Cluster Deployment</strong> is recommended for production business configurations to ensure high availability.</li><li>Dynamically scale capacity based on CPU, memory, and disk monitoring.</li><li>Use <strong>SSD</strong> or other high-performance storage.</li><li>Enable snapshots or backups to protect data.</li><li>Configure separate Management and Business networks.</li></ul>',18)])])}const g=e(n,[["render",l]]);export{h as __pageData,g as default};
