import{_ as e,c as n,o as a,ah as l}from"./chunks/framework.BDBjiFeZ.js";const m=JSON.parse('{"title":"Design Philosophy","description":"","frontmatter":{"sidebar_position":1},"headers":[],"relativePath":"en_US/introduction/design_ideas.md","filePath":"en_US/introduction/design_ideas.md"}'),s={name:"en_US/introduction/design_ideas.md"};function t(o,i,r,c,d,h){return a(),n("div",null,[...i[0]||(i[0]=[l('<h1 id="design-philosophy" tabindex="-1">Design Philosophy <a class="header-anchor" href="#design-philosophy" aria-label="Permalink to “Design Philosophy”">​</a></h1><p>&quot;Great truths are always simple.&quot; —— <em>Laozi</em></p><h2 id="design-goals" tabindex="-1">Design Goals <a class="header-anchor" href="#design-goals" aria-label="Permalink to “Design Goals”">​</a></h2><ul><li><strong>Simplicity</strong><ul><li>Example: Adding a device requires only one menu and one button; all device onboarding processes are centralized here.</li></ul></li><li><strong>Speed</strong><ul><li>Example: Through abstraction and encapsulation, Device Templates are formed, greatly improving reuse efficiency.</li></ul></li></ul><h2 id="design-mission" tabindex="-1">Design Mission <a class="header-anchor" href="#design-mission" aria-label="Permalink to “Design Mission”">​</a></h2><ul><li>Eliminate repetitive actions.</li><li>Reduce wasted time.</li><li>Enhance human-centric experience.</li></ul><h2 id="design-logic" tabindex="-1">Design Logic <a class="header-anchor" href="#design-logic" aria-label="Permalink to “Design Logic”">​</a></h2><ul><li>Design the system using common sense.</li><li>Solve complex problems with simple methods.</li><li>Higher-level abstraction.</li><li>Simplify the system with standardized components.</li></ul><h2 id="design-principles" tabindex="-1">Design Principles <a class="header-anchor" href="#design-principles" aria-label="Permalink to “Design Principles”">​</a></h2><h3 id="concepts-naming" tabindex="-1">Concepts &amp; Naming <a class="header-anchor" href="#concepts-naming" aria-label="Permalink to “Concepts &amp; Naming”">​</a></h3><ul><li><strong>MECE Principle</strong> (Mutually Exclusive, Collectively Exhaustive). Independent and exhaustive, making structural divisions logically clear.</li></ul><h3 id="openness-principle" tabindex="-1">Openness Principle <a class="header-anchor" href="#openness-principle" aria-label="Permalink to “Openness Principle”">​</a></h3><ul><li>All parallel functional items must be open and extensible. (Anywhere multiple options exist, this must be satisfied).</li></ul><h3 id="minimalism-principle" tabindex="-1">Minimalism Principle <a class="header-anchor" href="#minimalism-principle" aria-label="Permalink to “Minimalism Principle”">​</a></h3><ul><li>Achieve business goals with the fewest mouse clicks.</li><li>Achieve business goals with the least amount of code.</li><li>Achieve objectives with minimal cognitive load.</li><li>If possible, delete it.</li></ul><h3 id="consistency-principle" tabindex="-1">Consistency Principle <a class="header-anchor" href="#consistency-principle" aria-label="Permalink to “Consistency Principle”">​</a></h3><ul><li>Backward compatibility for features.</li><li>Do not change user habits.</li><li>Consistent interface usability style.</li></ul>',17)])])}const g=e(s,[["render",t]]);export{m as __pageData,g as default};
