import{_ as i,c as t,o as n,ah as a}from"./chunks/framework.BDBjiFeZ.js";const d=JSON.parse('{"title":"Features","description":"","frontmatter":{"sidebar_position":3},"headers":[],"relativePath":"en_US/introduction/functions.md","filePath":"en_US/introduction/functions.md"}'),l={name:"en_US/introduction/functions.md"};function o(r,e,s,c,g,u){return n(),t("div",null,[...e[0]||(e[0]=[a('<h1 id="features" tabindex="-1">Features <a class="header-anchor" href="#features" aria-label="Permalink to “Features”">​</a></h1><h2 id="i-system-architecture-deployment-environment" tabindex="-1">I. System Architecture &amp; Deployment Environment <a class="header-anchor" href="#i-system-architecture-deployment-environment" aria-label="Permalink to “I. System Architecture &amp; Deployment Environment”">​</a></h2><ol><li>Supports deployment on major operating systems including Windows, Linux, NeoKylin, UOS, Deepin, Kylin, NewStart, etc.</li><li>Supports various CPU architectures: x86, AMD64, ARMv7, ARMv8, ARM64.</li><li>B/S Architecture: Supports browser-based access for login, information viewing, device control, and data management.</li><li>Multiple installation methods: Windows Installer, One-click Script, Docker, K8s, Source Code.</li><li>Supports virtualization software.</li><li>Mobile App support for Android and iOS; can also be published as WeChat Mini Programs.</li><li>Supports Single-node and Cluster deployment (Cluster supports High Availability &amp; High Concurrency).</li><li>Compatible with open-source and domestic databases: PostgreSQL, TimescaleDB, TDengine, Tencent TDSQL, Alibaba PolarDB, KingBase, Dameng Database, etc.</li><li>High-performance Middleware: MQTT Brokers (EMQ, VerneMQ, GMQTT), Redis/NoSQL for caching.</li><li>Hardware agnostic: Deploys on everything from Set-top Boxes (1 Core 1G) and Raspberry Pis to large servers.</li><li><strong>Security</strong>: Compliant with <strong>MLPS Level 3</strong>.</li><li>Performance: Supports millisecond-level high-frequency and million-point high-concurrency data writes.</li><li>Supports AI Algorithm Center, integrating various algorithms, large models, and Agents for predictive maintenance, equipment failure prediction, energy optimization, etc.</li></ol><h2 id="ii-admin-management-functions" tabindex="-1">II. Admin Management Functions <a class="header-anchor" href="#ii-admin-management-functions" aria-label="Permalink to “II. Admin Management Functions”">​</a></h2><ol><li><p><strong>Global Dashboard</strong>:</p><ul><li>Real-time monitoring of system resources (CPU, Memory, Disk).</li><li>Display comprehensive data: Tenant config, Total devices, Online/Offline devices, Alerts.</li></ul></li><li><p><strong>Device Connectivity Service Management</strong>:</p><ul><li>Manages <strong>Device Connectivity Services</strong> and <strong>Third-party Integration Services</strong>.</li><li>Supports adding new custom services for protocol extension.</li><li>Custom MQTT service addresses and subscription topic prefixes.</li><li>Custom service identifiers and version numbers.</li><li>Service status monitoring (Running/Paused).</li></ul></li><li><p><strong>Visualization Management</strong>:</p><ul><li>Set default dashboard for Super Admin.</li><li>Set custom dashboard as homepage.</li></ul></li><li><p><strong>System Management</strong>:</p><ul><li><strong>Tenant Config</strong>: Multi-level tenant management. <ul><li>Resource limits (Device count, Daily messages, Data retention days).</li></ul></li><li><strong>Tenant Management</strong>: Edit tenant info, impersonate tenant account. <ul><li>Tenant password reset.</li><li>Tenant Freeze/Unfreeze.</li><li>Tenant Search (Email, Name, Status).</li></ul></li><li><strong>System Settings</strong>: Edit System Title, Logo, Background. <ul><li>Data Cleanup Policies.</li><li>Tenant Registration Control, Captcha Login, Frontend Encryption.</li></ul></li><li><strong>Menu Management</strong>: Set global menu order, routing, names. <ul><li>Show/Hide menus.</li><li>Menu permissions (System Admin / Tenant).</li></ul></li><li><strong>Notification Service</strong>: Email and SMS configuration. <ul><li>Push message management.</li></ul></li></ul></li></ol><h2 id="iii-tenant-management-functions" tabindex="-1">III. Tenant Management Functions <a class="header-anchor" href="#iii-tenant-management-functions" aria-label="Permalink to “III. Tenant Management Functions”">​</a></h2><h3 id="_1-functional-structure" tabindex="-1">1. Functional Structure <a class="header-anchor" href="#_1-functional-structure" aria-label="Permalink to “1. Functional Structure”">​</a></h3><p>Includes: Dashboard Overview, Device Connectivity, Visualization, Automation, Alerts, Product Management, Data Services, System Management.</p><h3 id="_2-registration-login" tabindex="-1">2. Registration &amp; Login <a class="header-anchor" href="#_2-registration-login" aria-label="Permalink to “2. Registration &amp; Login”">​</a></h3><ol><li><strong>Login Methods</strong>: Phone, Email, etc.</li><li>Password Recovery.</li></ol><h3 id="_3-device-management" tabindex="-1">3. Device Management <a class="header-anchor" href="#_3-device-management" aria-label="Permalink to “3. Device Management”">​</a></h3><ol><li><p><strong>Search &amp; Filter</strong>:</p><ul><li>Filter by Group, Device Template, Online Status, Alert Status.</li><li>Filter by Device Type (Direct, Gateway, Sub-device).</li><li>Filter by Access Method (Protocol/Service).</li><li>Search by Name, Tag.</li><li><strong>Device Map</strong> view.</li></ul></li><li><p><strong>Protocol Support</strong>:</p><ul><li>MQTT, Modbus RTU, Modbus TCP.</li><li>China Mobile OneNET, China Telecom CTWing.</li><li>GB28181 (National Standard Video Security).</li><li>Hikvision Integrated Security Platform.</li><li>OPC UA.</li><li>Beidou Satellite 2/3.</li><li>Hydrology protocols, Water Resource protocols, IEC104.</li><li>GB26875.3-2011 Alarm Transmission Protocol.</li></ul></li><li><p><strong>Device Onboarding</strong>:</p><ul><li>Manual, Protocol-based, Code-based, Service-based addition.</li><li>Third-party platform device addition.</li><li><strong>Unified Entry</strong>: Global devices added via a single method, covering direct protocol, third-party integration, gateways, sub-devices. Supports cameras, sensors, GPS, etc.</li><li><strong>New Protocols</strong>: Add new protocols by developing a new Device Connectivity Service without changing the core platform.</li><li>Supports Sequential Onboarding and Pre-configuration mental models.</li></ul></li><li><p><strong>Device Details Management</strong> (10 Tabs):</p><ul><li><strong>Telemetry</strong>: <ul><li>Command downlink.</li><li>Operation logs.</li><li>Data simulation.</li><li>List/Curve display.</li><li>Time ranges (5 mins to 1 year).</li><li>JSON/Binary data reporting.</li><li>Aggregation (1 min to 1 month): Max, Min, Avg, Diff, Count.</li><li>Data Export.</li><li>Line, Bar, Scatter charts.</li></ul></li><li><strong>Charts</strong>: Render charts based on Device Model configuration. <ul><li>Supports Device SCADA/Topology.</li></ul></li><li><strong>Connection</strong>: View parameters (Server, Port, Auth, Topics).</li><li><strong>Info</strong>: Manage device location, maintenance info, custom fields.</li><li><strong>Properties</strong>: View reported properties, set writable properties.</li><li><strong>Events</strong>: Alerts, Battery levels, special events.</li><li><strong>Commands</strong>: Remote control. <ul><li>Create common commands.</li><li>Quick command execution.</li></ul></li><li><strong>Shadow/Desired Messages</strong>: Telemetry, Property, Command types. <ul><li>Offline message caching.</li><li>Expiration settings.</li><li>Message status tracking.</li></ul></li><li><strong>Automation</strong>: Create per-device rules (Alerts, Schedules, Scenes).</li><li><strong>Users</strong>: Add/Remove associated users.</li><li><strong>Settings</strong>: Change Device Template. <ul><li>Manually adjust online status.</li><li>View firmware info.</li></ul></li><li><strong>Diagnosis</strong>: Visualized device communication health monitoring. <ul><li>Statistical Indicators (Uplink/Downlink/Storage success rates).</li><li>Failure Record Tracking.</li></ul></li></ul></li><li><p><strong>Device Groups</strong>:</p><ul><li>One device can belong to multiple groups.</li><li>Create, Search, View, Delete groups.</li><li>Sub-groups.</li><li>Multi-industry hierarchy adaptation (Agriculture, Environmental, Industrial, etc.).</li></ul></li></ol><h3 id="_4-device-template-management" tabindex="-1">4. Device Template Management <a class="header-anchor" href="#_4-device-template-management" aria-label="Permalink to “4. Device Template Management”">​</a></h3><ol><li><p><strong>Create Template</strong>:</p><ul><li>Custom Name.</li><li>Select Device Model.</li><li>Support Direct/Gateway/Sub-device.</li><li>Support all protocols and third-party integrations.</li></ul></li><li><p><strong>Template Functions</strong> (8 Tabs):</p><ul><li><strong>Associated Devices</strong>: Batch add/remove.</li><li><strong>Attributes &amp; Functions</strong>: Operate directly on bound Device Model.</li><li><strong>Protocol Config</strong>: Protocol selection.</li><li><strong>Data Processing</strong>: Telemetry Uplink/Downlink preprocessing. <ul><li>Lua Script support.</li><li>Real-time processing (e.g., Rounding, Fault Prediction via Voltage, Power Calculation via V*I).</li></ul></li><li><strong>Automation</strong>: Batch automation for devices using the template.</li><li><strong>Alerts</strong>: Batch alert rules.</li><li><strong>Extensions</strong>: Define custom fields (String/Bool/Number) with default values and auto-generated forms.</li><li><strong>Device Settings</strong>: <ul><li><strong>One Device One Secret</strong> / Certificate fetching.</li><li>Online Strategy (Timeout, Heartbeat).</li><li>Auto-create device config.</li><li>Copy Password.</li><li>Delete Template.</li></ul></li></ul></li></ol><h3 id="_5-device-model-management" tabindex="-1">5. Device Model Management <a class="header-anchor" href="#_5-device-model-management" aria-label="Permalink to “5. Device Model Management”">​</a></h3><ol><li><p><strong>Basic Info</strong>: Name, Tags, Author, Version.</p></li><li><p><strong>Field Definitions</strong>:</p><ul><li><strong>Telemetry</strong>: String, Number, Bool, Enum. R/W permissions, Units.</li><li><strong>Properties</strong>: String, Number, Bool, Enum. R/W permissions, Units.</li><li><strong>Events</strong>: Add parameters (String, Number, Bool). Multiple events.</li><li><strong>Commands</strong>: String, Number, Bool, Enum. Common commands.</li></ul></li><li><p><strong>Chart Configuration</strong>:</p><ul><li>Add charts (Line, Digital Indicator, Value Downlink, Enum, Gauge, Card, Table, Text, Video).</li><li>Configure Mobile App Device Details.</li></ul></li><li><p><strong>Export</strong>: Export to JSON.</p></li></ol><h3 id="_6-service-access" tabindex="-1">6. Service Access <a class="header-anchor" href="#_6-service-access" aria-label="Permalink to “6. Service Access”">​</a></h3><ul><li>Connect third-party platforms.</li><li>Integrate external device data.</li></ul><h2 id="iv-visualization-functions" tabindex="-1">IV. Visualization Functions <a class="header-anchor" href="#iv-visualization-functions" aria-label="Permalink to “IV. Visualization Functions”">​</a></h2><h3 id="_1-visualization-screen-big-screen-management" tabindex="-1">1. Visualization Screen (Big Screen) Management <a class="header-anchor" href="#_1-visualization-screen-big-screen-management" aria-label="Permalink to “1. Visualization Screen (Big Screen) Management”">​</a></h3><ol><li><strong>Basic Functions</strong>: New, Edit, Preview, Search, Grouping.</li><li><strong>Editor</strong>: Layer Management, Z-Index.</li><li><strong>Components</strong>: <ul><li><strong>Basic</strong>: Text, Number, Marquee, Time, Countdown, Link, Flipper, Input, Button, Switch, Decorators.</li><li><strong>Charts</strong>: Line, Bar, Pie, Radar, Scatter, Funnel, Sankey, K-Line, Water Wave, Gauge.</li><li><strong>3D</strong>: 3D Bar, 3D Curve.</li><li><strong>Visuals</strong>: Video, Image, Icons (Industrial, Chemical, Agricultural, etc.).</li><li><strong>Custom Components</strong>: Business/System components.</li></ul></li><li><strong>Page Editor</strong>: Zoom, Adaptive, Align, History, Snapshot, Resolution settings, Magnetism, Theme.</li></ol><h3 id="_2-dashboard-functions" tabindex="-1">2. Dashboard Functions <a class="header-anchor" href="#_2-dashboard-functions" aria-label="Permalink to “2. Dashboard Functions”">​</a></h3><ol><li><strong>Basic Management</strong>: New, Search, Set as Home, Edit, Drag &amp; Drop.</li><li><strong>Themes</strong>: Minimal White, Tech Blue, Apple Purple, Microsoft Style, Cool Black.</li><li><strong>Components</strong>: System (Device counts, trends), Devices, Plugins, Charts. <ul><li>Data binding.</li><li>Property settings.</li></ul></li></ol><h2 id="v-automation-functions" tabindex="-1">V. Automation Functions <a class="header-anchor" href="#v-automation-functions" aria-label="Permalink to “V. Automation Functions”">​</a></h2><h3 id="_1-scene-linkage-automation" tabindex="-1">1. Scene Linkage (Automation) <a class="header-anchor" href="#_1-scene-linkage-automation" aria-label="Permalink to “1. Scene Linkage (Automation)”">​</a></h3><ol><li><strong>General</strong>: Create, Search.</li><li><strong>Triggers</strong>: <ul><li><strong>Device</strong>: Single Device, Device Category, Group.</li><li><strong>Time</strong>: One-time, Recurring (Hourly/Daily/Weekly/Monthly), Cron.</li></ul></li><li><strong>Actions</strong>: Control Device, Activate Scene, Trigger Alert.</li></ol><h3 id="_2-scene-management" tabindex="-1">2. Scene Management <a class="header-anchor" href="#_2-scene-management" aria-label="Permalink to “2. Scene Management”">​</a></h3><ol><li><strong>General</strong>: Create, Search, List.</li><li><strong>Operations</strong>: Activate, Edit, Logs, Delete.</li><li><strong>Editing</strong>: Batch control multiple devices.</li></ol><h3 id="_3-notification-management" tabindex="-1">3. Notification Management <a class="header-anchor" href="#_3-notification-management" aria-label="Permalink to “3. Notification Management”">​</a></h3><ol><li><strong>Logs</strong>: View Email/SMS/Voice/External notifications. Status tracking.</li><li><strong>Notification Groups</strong>: Create groups, select methods (Email, Voice, SMS), Enable/Disable.</li></ol><h2 id="vi-alert-functions" tabindex="-1">VI. Alert Functions <a class="header-anchor" href="#vi-alert-functions" aria-label="Permalink to “VI. Alert Functions”">​</a></h2><ol><li><strong>Alert Info</strong>: Time, Name, Status, Content, Description. Handle alerts.</li><li><strong>Alert Config</strong>: Name, Level, Notification Group, Enable/Disable.</li></ol><h2 id="vii-product-management" tabindex="-1">VII. Product Management <a class="header-anchor" href="#vii-product-management" aria-label="Permalink to “VII. Product Management”">​</a></h2><ol><li><p><strong>Product List</strong>:</p><ul><li>Add Product (Select Template).</li><li>Pre-registration.</li><li>Supports <strong>One Product One Secret</strong> and <strong>One Device One Secret</strong>.</li></ul></li><li><p><strong>Pre-registration</strong>: Batch creation, Export, Batch upload.</p></li><li><p><strong>OTA Upgrade</strong>:</p><ul><li>Firmware Packages (Diff/Full).</li><li>Version Management.</li><li>Tasks &amp; Progress Monitoring.</li><li>Signature Algorithms.</li></ul></li></ol><h2 id="viii-data-services" tabindex="-1">VIII. Data Services <a class="header-anchor" href="#viii-data-services" aria-label="Permalink to “VIII. Data Services”">​</a></h2><ol><li><strong>Data Forwarding</strong>: <ul><li>Create Rules (Enable/Edit/Delete).</li><li>Sources: Telemetry, Property, Event, Online/Offline.</li><li>Scripting with parameters &amp; debugging.</li><li>Targets: MQTT, HTTP.</li><li>Multi-cast.</li><li>Integration with FUXA SCADA.</li></ul></li></ol><h2 id="ix-system-management" tabindex="-1">IX. System Management <a class="header-anchor" href="#ix-system-management" aria-label="Permalink to “IX. System Management”">​</a></h2><ol><li><strong>Open API</strong>: API Key management.</li><li><strong>System Logs</strong>: Audit trails (User, IP, Path, Method, Time). Device Logs.</li><li><strong>Role Management</strong>: RBAC, Page-level permissions.</li><li><strong>User Management</strong>: Add, Edit, Reset Password, Roles, Freeze.</li></ol><h2 id="x-mobile-app-functions" tabindex="-1">X. Mobile App Functions <a class="header-anchor" href="#x-mobile-app-functions" aria-label="Permalink to “X. Mobile App Functions”">​</a></h2><ol><li><strong>Basic</strong>: Device List, Groups, Details, Control, Generated UI.</li><li><strong>Scenes</strong>: Manage Automation.</li><li><strong>Alerts</strong>: View Alerts.</li><li><strong>Onboarding</strong>: Scan QR Code.</li></ol><h2 id="xi-technical-characteristics" tabindex="-1">XI. Technical Characteristics <a class="header-anchor" href="#xi-technical-characteristics" aria-label="Permalink to “XI. Technical Characteristics”">​</a></h2><ol><li><strong>Protocols</strong>: MQTT, TCP, HTTP, Modbus, GB28181, OPC UA, Beidou, Hydrology, IEC104, GB26875.3. Custom Protocols.</li><li><strong>Integrations</strong>: Hikvision, OneNET, CTWING, ChirpStack.</li><li><strong>Advantages</strong>: Ease of Use, Compatibility, Componentization.</li><li><strong>Tech Stack</strong>: <ul><li><strong>Golang</strong>: High concurrency, low footprint.</li><li><strong>Vue.js 3</strong>.</li><li><strong>Node.js</strong>.</li><li><strong>Databases</strong>: PostgreSQL, TimescaleDB, TDengine, Cassandra, TDSQL, PolarDB, KingBase.</li><li><strong>Nginx</strong>.</li><li><strong>Brokers</strong>: GMQTT, VerneMQ.</li><li><strong>Cache</strong>: Redis.</li><li><strong>Monitoring</strong>: Prometheus.</li><li><strong>Security</strong>: DeepSource.</li></ul></li></ol><h2 id="xii-performance-metrics" tabindex="-1">XII. Performance Metrics <a class="header-anchor" href="#xii-performance-metrics" aria-label="Permalink to “XII. Performance Metrics”">​</a></h2><ul><li><strong>Concurrent Users</strong>: 100 - 100,000.</li><li><strong>Device Scale</strong>: Single node 5,000 pts/sec, Cluster 1M+ pts.</li><li><strong>Throughput</strong>: 500k msg/sec.</li><li><strong>Daily Capacity</strong>: &gt; 100M messages.</li><li><strong>Latency</strong>: &lt; 1s (Page Load &amp; Query).</li><li><strong>Availability</strong>: &gt; 99.99%.</li><li><strong>Retention</strong>: Automatic cleanup.</li></ul><h2 id="xiii-secondary-development" tabindex="-1">XIII. Secondary Development <a class="header-anchor" href="#xiii-secondary-development" aria-label="Permalink to “XIII. Secondary Development”">​</a></h2><ul><li>Online Documentation.</li><li>Open API Docs.</li><li>Development Templates.</li><li><strong>LLM/AI Integration</strong>: Supports MCP protocol, integration with DeepSeek, Qwen, ChatGLM for &quot;Thinking IoT&quot;.</li><li><strong>AIoT</strong>: Supports ESP32 with voice capabilities (ASR/TTS/LLM) for Human-Machine Interaction.</li></ul>',46)])])}const p=i(l,[["render",o]]);export{d as __pageData,p as default};
