import{_ as i,c as n,o as t,ah as a,cy as o,cz as r}from"./chunks/framework.BDBjiFeZ.js";const v=JSON.parse('{"title":"Device Connectivity Service Manage","description":"","frontmatter":{"sidebar_position":1},"headers":[],"relativePath":"en_US/user-guide/system-admin-operation-manual/plugin-managment/plugin-management.md","filePath":"en_US/user-guide/system-admin-operation-manual/plugin-managment/plugin-management.md"}'),c={name:"en_US/user-guide/system-admin-operation-manual/plugin-managment/plugin-management.md"};function s(l,e,p,d,m,g){return t(),n("div",null,[...e[0]||(e[0]=[a('<h1 id="device-connectivity-service-manage" tabindex="-1">Device Connectivity Service Manage <a class="header-anchor" href="#device-connectivity-service-manage" aria-label="Permalink to “Device Connectivity Service Manage”">​</a></h1><h2 id="description" tabindex="-1">Description <a class="header-anchor" href="#description" aria-label="Permalink to “Description”">​</a></h2><p>Device Connectivity Service Management includes Device Connectivity Service and Third-party Device Connectivity Service. Device Connectivity Service connects devices directly, while Third-party Service connects devices via third-party platforms.</p><h2 id="role-of-device-connectivity-plugins" tabindex="-1">Role of Device Connectivity Plugins <a class="header-anchor" href="#role-of-device-connectivity-plugins" aria-label="Permalink to “Role of Device Connectivity Plugins”">​</a></h2><p><strong>Compatibility and Interoperability</strong>: By supporting multiple communication protocols and platforms, the Device Connectivity Service ensures compatibility and interoperability between different devices, which is key to the success of the IoT ecosystem.</p><p><strong>Scalability</strong>: As new technologies and standards emerge, IoT systems may need to support new communication protocols. Device Connectivity Plugins allow the system to easily extend its functionality by simply adding new plugins without redesigning the entire system.</p><p><strong>Modularity</strong>: Device Connectivity Plugins provide a modular approach to handling communication tasks, making system maintenance and upgrades easier.</p><p><strong>Customization</strong>: Different applications may require different communication behaviors or optimizations. The Device Connectivity Service allows developers to customize or optimize specific communication protocols to meet specific application needs.</p><h2 id="steps" tabindex="-1">Steps <a class="header-anchor" href="#steps" aria-label="Permalink to “Steps”">​</a></h2><ul><li>Click App Management - Plugin Management to enter the protocol management page. <img src="'+o+'" alt="Plugin List"></li><li>Device Connectivity Services can only be added by System Administrators. You need to log in as a System Administrator.</li><li>Currently supports creation, editing, and deletion of a protocol. <img src="'+r+'" alt="Operation"></li></ul><h2 id="configuration-reference" tabindex="-1">Configuration Reference <a class="header-anchor" href="#configuration-reference" aria-label="Permalink to “Configuration Reference”">​</a></h2><ul><li>Please refer to the README of each protocol plugin in the code repository to configure and understand the Device Connectivity Service. <a href="./../../../introduction/code_repository.html"><code>Code Respository</code></a></li></ul>',12)])])}const y=i(c,[["render",s]]);export{v as __pageData,y as default};
