import{_ as a,c as l,o as i,ah as r}from"./chunks/framework.BDBjiFeZ.js";const u=JSON.parse('{"title":"开发环境","description":"","frontmatter":{"sidebar_position":1},"headers":[],"relativePath":"zh_CN/developer-guide/devEnv.md","filePath":"zh_CN/developer-guide/devEnv.md"}'),o={name:"zh_CN/developer-guide/devEnv.md"};function t(n,e,d,s,h,c){return i(),l("div",null,[...e[0]||(e[0]=[r('<h1 id="开发环境" tabindex="-1">开发环境 <a class="header-anchor" href="#开发环境" aria-label="Permalink to “开发环境”">​</a></h1><p>为了进行源码的二次开发，建议配置以下开发环境和工具。</p><h2 id="编码工具" tabindex="-1">编码工具 <a class="header-anchor" href="#编码工具" aria-label="Permalink to “编码工具”">​</a></h2><h3 id="idea" tabindex="-1">IDEA: <a class="header-anchor" href="#idea" aria-label="Permalink to “IDEA:”">​</a></h3><ul><li><strong>IntelliJ IDEA</strong> 是一款强大的集成开发环境，适用于Java、Kotlin、Groovy等语言的开发。</li><li>支持多种插件和工具，提高开发效率。</li><li>提供代码自动完成、重构、快速导航等功能。</li><li>官网下载链接: <a href="https://www.jetbrains.com/idea/" target="_blank" rel="noreferrer">IntelliJ IDEA</a></li></ul><h3 id="vscode" tabindex="-1">VSCODE: <a class="header-anchor" href="#vscode" aria-label="Permalink to “VSCODE:”">​</a></h3><ul><li><strong>Visual Studio Code</strong> 是一款轻量级但功能强大的源代码编辑器，支持多种编程语言。</li><li>内置Git支持，方便代码版本控制。</li><li>丰富的插件生态，可自定义开发环境。</li><li>官网下载链接: <a href="https://code.visualstudio.com/" target="_blank" rel="noreferrer">Visual Studio Code</a></li></ul><h2 id="win环境搭建" tabindex="-1">Win环境搭建 <a class="header-anchor" href="#win环境搭建" aria-label="Permalink to “Win环境搭建”">​</a></h2><h3 id="后端" tabindex="-1">后端 <a class="header-anchor" href="#后端" aria-label="Permalink to “后端”">​</a></h3><ul><li><strong>Go 1.18x</strong><ul><li>Go语言是一种编译型、具有垃圾回收功能的编程语言，适合并发处理和快速开发。</li><li>安装步骤: <ol><li>访问Go语言官方网站: <a href="https://golang.org/" target="_blank" rel="noreferrer">Go Programming Language</a></li><li>下载适用于Windows的Go 1.18x安装包。</li><li>按照安装向导完成安装。</li><li>设置环境变量<code>GOPATH</code>和<code>GOROOT</code>。</li><li>验证安装：在命令行中运行<code>go version</code>。</li></ol></li></ul></li></ul><h3 id="前端" tabindex="-1">前端 <a class="header-anchor" href="#前端" aria-label="Permalink to “前端”">​</a></h3><ul><li><strong>Node.js 16.14</strong><ul><li>Node.js是一个基于Chrome V8引擎的JavaScript运行环境。</li><li>安装步骤: <ol><li>访问Node.js官方网站: <a href="https://nodejs.org/" target="_blank" rel="noreferrer">Node.js</a></li><li>下载适用于Windows的Node.js 16.14版本。</li><li>按照安装向导完成安装。</li><li>验证安装：在命令行中运行<code>node -v</code>和<code>npm -v</code>。</li></ol></li></ul></li></ul>',12)])])}const g=a(o,[["render",t]]);export{u as __pageData,g as default};
