import{_ as a,c as e,o as l,ah as r}from"./chunks/framework.BDBjiFeZ.js";const _=JSON.parse('{"title":"环境要求","description":"","frontmatter":{"sidebar_position":1},"headers":[],"relativePath":"zh_CN/installation/environmental_requirement.md","filePath":"zh_CN/installation/environmental_requirement.md"}'),d={name:"zh_CN/installation/environmental_requirement.md"};function i(n,t,h,o,s,b){return l(),e("div",null,[...t[0]||(t[0]=[r('<h1 id="环境要求" tabindex="-1">环境要求 <a class="header-anchor" href="#环境要求" aria-label="Permalink to “环境要求”">​</a></h1><h2 id="硬件设备要求" tabindex="-1">硬件设备要求 <a class="header-anchor" href="#硬件设备要求" aria-label="Permalink to “硬件设备要求”">​</a></h2><h3 id="最低测试配置" tabindex="-1">最低测试配置 <a class="header-anchor" href="#最低测试配置" aria-label="Permalink to “最低测试配置”">​</a></h3><p>ThingsPanel整体内存的占用约100M以下，安装必要服务后会达到几百M。最小可使用1G内存设备进行测试，适合设备数量较少的测试环境。</p><table tabindex="0"><thead><tr><th>项目</th><th>配置要求</th></tr></thead><tbody><tr><td>CPU</td><td>1核心 2.0GHz+</td></tr><tr><td>内存</td><td>1GB</td></tr><tr><td>硬盘</td><td>20G</td></tr></tbody></table><h3 id="研发测试配置" tabindex="-1">研发测试配置 <a class="header-anchor" href="#研发测试配置" aria-label="Permalink to “研发测试配置”">​</a></h3><table tabindex="0"><thead><tr><th>项目</th><th>配置要求</th></tr></thead><tbody><tr><td>CPU</td><td>2核+ 2.5GHz+</td></tr><tr><td>内存</td><td>4GB+</td></tr><tr><td>硬盘</td><td>40GB+</td></tr></tbody></table><h3 id="生产环境配置" tabindex="-1">生产环境配置 <a class="header-anchor" href="#生产环境配置" aria-label="Permalink to “生产环境配置”">​</a></h3><p>推荐采用集群部署模式，确保系统高可用性：</p><table tabindex="0"><thead><tr><th>项目</th><th>最小配置要求</th><th>建议配置</th></tr></thead><tbody><tr><td>CPU</td><td>4核+ 2.5GHz+</td><td>8核+ 3.0GHz+</td></tr><tr><td>内存</td><td>8GB+</td><td>16GB+</td></tr><tr><td>硬盘</td><td>40GB+ SSD</td><td>100GB+ SSD</td></tr><tr><td>服务器数量</td><td>2-3台</td><td>3-5台</td></tr></tbody></table><h4 id="cpu架构支持" tabindex="-1">CPU架构支持 <a class="header-anchor" href="#cpu架构支持" aria-label="Permalink to “CPU架构支持”">​</a></h4><ul><li>x86架构：支持Intel、AMD等主流服务器CPU</li><li>ARM架构：支持飞腾、鲲鹏等国产CPU</li><li>RISC-V：支持兆芯、海光等国产CPU</li></ul><h4 id="服务器架构要求" tabindex="-1">服务器架构要求 <a class="header-anchor" href="#服务器架构要求" aria-label="Permalink to “服务器架构要求”">​</a></h4><ul><li>支持主流服务器架构：塔式、机架式、刀片式</li><li>推荐使用机架式服务器，便于集中管理和扩展</li><li>建议配置冗余电源和硬盘阵列(RAID)</li></ul><h4 id="国产化适配说明" tabindex="-1">国产化适配说明 <a class="header-anchor" href="#国产化适配说明" aria-label="Permalink to “国产化适配说明”">​</a></h4><ul><li>操作系统： <ul><li>支持统信UOS、麒麟等国产操作系统</li><li>支持欧拉、OpenEuler等国产服务器操作系统</li></ul></li><li>数据库： <ul><li>支持阿里云PolarDB,人大金仓等国产数据库</li></ul></li></ul><h3 id="补充建议" tabindex="-1">补充建议 <a class="header-anchor" href="#补充建议" aria-label="Permalink to “补充建议”">​</a></h3><ul><li>业务配置推荐采用集群方式部署，确保高可用性</li><li>基于对CPU和内存、硬盘的监控来动态扩容</li><li>推荐使用SSD等高性能存储设备</li><li>建议开启快照或备份功能保护数据</li><li>建议配置独立的管理网络和业务网络</li></ul>',18)])])}const m=a(d,[["render",i]]);export{_ as __pageData,m as default};
