import{_ as i,c as l,o as e,ah as r}from"./chunks/framework.BDBjiFeZ.js";const u=JSON.parse('{"title":"设计理念","description":"","frontmatter":{"sidebar_position":1},"headers":[],"relativePath":"zh_CN/introduction/design_ideas.md","filePath":"zh_CN/introduction/design_ideas.md"}'),n={name:"zh_CN/introduction/design_ideas.md"};function t(h,a,d,s,o,c){return e(),l("div",null,[...a[0]||(a[0]=[r('<h1 id="设计理念" tabindex="-1">设计理念 <a class="header-anchor" href="#设计理念" aria-label="Permalink to “设计理念”">​</a></h1><p>大道至简、大音希声、大智若愚、大象无形、大巧若拙</p><p>——出自《老子》</p><h2 id="设计目标" tabindex="-1">设计目标 <a class="header-anchor" href="#设计目标" aria-label="Permalink to “设计目标”">​</a></h2><ul><li>简单 <ul><li>例如：添加设备时，只需要通过一个菜单，一个按钮，所有的添加设备都集中到了这里。</li></ul></li><li>快速 <ul><li>例如：通过抽象和封装的方法，形成设备模板，大大提高复用效率。</li></ul></li></ul><h2 id="设计宗旨" tabindex="-1">设计宗旨 <a class="header-anchor" href="#设计宗旨" aria-label="Permalink to “设计宗旨”">​</a></h2><ul><li>消灭重复的动作</li><li>减少生命的浪费</li><li>增进人性化体验</li></ul><h2 id="设计思路" tabindex="-1">设计思路 <a class="header-anchor" href="#设计思路" aria-label="Permalink to “设计思路”">​</a></h2><ul><li>用常识思维设计系统。</li><li>用简单的办法解决复杂的问题。</li><li>更高层次的抽象。</li><li>用标准化组件简化系统。</li></ul><h2 id="设计原则" tabindex="-1">设计原则 <a class="header-anchor" href="#设计原则" aria-label="Permalink to “设计原则”">​</a></h2><h3 id="概念与命名" tabindex="-1">概念与命名 <a class="header-anchor" href="#概念与命名" aria-label="Permalink to “概念与命名”">​</a></h3><ul><li>MECE穷尽和互斥原则。相互独立、完全穷尽，让结构划分更加逻辑清晰。</li></ul><h3 id="开放原则" tabindex="-1">开放原则 <a class="header-anchor" href="#开放原则" aria-label="Permalink to “开放原则”">​</a></h3><ul><li>凡并列功能项都要开放，可扩展。（凡是有多个选项的，都需要满足）</li></ul><h3 id="最小化原则" tabindex="-1">最小化原则 <a class="header-anchor" href="#最小化原则" aria-label="Permalink to “最小化原则”">​</a></h3><ul><li>用最少的鼠标点击实现业务。</li><li>用最少的代码实现业务。</li><li>动最少的脑子实现目的。</li><li>如果可能，尽量删掉。</li></ul><h3 id="一致性原则" tabindex="-1">一致性原则 <a class="header-anchor" href="#一致性原则" aria-label="Permalink to “一致性原则”">​</a></h3><ul><li>功能向后兼容</li><li>不改变用户的使用习惯</li><li>一致的界面易用性风格</li></ul>',18)])])}const b=i(n,[["render",t]]);export{u as __pageData,b as default};
